"use client"

import { useEffect, useRef, useState } from "react"
import { Volume2, VolumeX } from "lucide-react"
import Header from "../components/Header"
import Footer from "../components/Footer"
import Link from "next/link"

export default function AboutUsPage() {
  const videoRef = useRef<HTMLVideoElement>(null)
  const [muted, setMuted] = useState(true)
  const [gifIndex, setGifIndex] = useState(0)
  const [visibleLetters, setVisibleLetters] = useState(0)
  const videoSources = [
    "/images/about-video.mp4",
    
  ]
  const [sourceIndex, setSourceIndex] = useState(0)

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.muted = muted
      const playPromise = videoRef.current.play()
      if (playPromise && typeof playPromise.then === "function") {
        playPromise.catch(() => {
          // Some browsers block autoplay even when muted; try reload
          videoRef.current?.load()
          videoRef.current?.play().catch(() => {})
        })
      }
    }
  }, [muted, sourceIndex])

  // Auto-scroll GIFs functionality
  useEffect(() => {
    const interval = setInterval(() => {
      setGifIndex((prev) => (prev + 1) % 5) // 7 GIFs total, showing 3 at a time = 5 possible starting positions
    }, 5000) // Change every 5 seconds

    return () => clearInterval(interval)
  }, [])

 // Letter reveal animation on scroll
useEffect(() => {
  const target = document.getElementById("text-reveal-section")
  const text = "We shape meaningful musical journeys — crafting personalised learning experiences that connect teachers and learners in creative, powerful, and lasting ways."
  const totalLetters = text.length

  const handleScroll = () => {
    if (!target) return

    const rect = target.getBoundingClientRect()
    const windowHeight = window.innerHeight

    // Reveal only when section starts to appear
    const progress = Math.min(
      Math.max(0, windowHeight - rect.top),
      windowHeight
    ) / windowHeight

    const lettersToShow = Math.floor(progress * totalLetters * 1.5) // smoother effect
    setVisibleLetters(Math.min(lettersToShow, totalLetters))
  }

  window.addEventListener("scroll", handleScroll)
  handleScroll()
  return () => window.removeEventListener("scroll", handleScroll)
}, [])


  const gifs = [
    {
      src: "/images/about-us/Peronalise-Learning-3501.gif",
      title: "PERSONALIZED LEARNING EXPERIENCE",
      description: "Your lessons are tailored to your instrument, genre, budget, location, and schedule—music that fits your life."
    },
    {
      src: "/images/about-us/diverse-offerring-350.gif", 
      title: "DIVERSE OFFERINGS",
      description: "Learn everything from piano and trumpet to DJing, production, and composition—all in one place."
    },
    {
      src: "/images/about-us/For-all-skills01.gif",
      title: "FOR ALL SKILL LEVELS", 
      description: "Whether you're starting out or refining your craft, Maestera connects you with the right teacher."
    },
    {
      src: "/images/about-us/Global-Reach.gif",
      title: "GLOBAL AND LOCAL ACCESS",
      description: "Find teachers near you or around the world—wherever you prefer to learn."
    },
    {
      src: "/images/about-us/Hlistic-approach01.gif",
      title: "HOLISTIC AND INCLUSIVE APPROACH",
      description: "Explore performance, theory, and genres from Indian classical to jazz and experimental music."
    },
    {
      src: "/images/about-us/learn-at-your-convenience-350.gif",
      title: "SUPPORT EVERY STEP OF THE WAY",
      description: "Can't find a teacher? We'll personally help match you with one who fits your needs."
    },
    {
      src: "/images/about-us/Peronalise-Learning-3501.gif",
      title: "LEARN AT YOUR CONVENIENCE",
      description: "Flexible scheduling and online options make learning easy—anytime, anywhere."
    }
  ]

  // Function to render text with letter-by-letter reveal
  const renderTextWithReveal = () => {
    const text = "We shape meaningful musical journeys — crafting personalised learning experiences that connect teachers and learners in creative, powerful, and lasting ways."
    const letters = text.split('')
    
    return letters.map((letter, index) => (
      <span
        key={index}
        className={`transition-colors duration-100 ${
          index < visibleLetters ? 'text-white' : 'text-gray-600'
        }`}
      >
        {letter}
      </span>
    ))
  }

  return (
    <main className="relative min-h-screen bg-black text-white">
      <Header />

      {/* Background Video */}
      <div className="absolute inset-0 z-0 overflow-hidden bg-black h-screen pointer-events-none">
        <video
          ref={videoRef}
          className="absolute top-0 left-1/2 -translate-x-1/2 h-screen w-auto object-contain object-top"
          autoPlay
          loop
          playsInline
          muted
          preload="auto"
          src={videoSources[sourceIndex]}
          onError={() => {
            setSourceIndex((prev) => (prev + 1 < videoSources.length ? prev + 1 : prev))
          }}
        />
      </div>

      {/* Spacer so split section starts after the video viewport */}
      <div className="h-screen" />

      {/* Split Section below video - sticky left, scrolling right */}
      <section className="relative z-20 grid grid-cols-1 md:grid-cols-2">
        {/* Left half: black with logo and tagline (sticky) */}
        <div className="bg-black text-white p-8 sticky top-0 h-screen flex items-center justify-center">
          <div className="flex flex-col items-center text-center">
            <img src="/images/about-logo.png" alt="About Maestera" className="w-100 h-auto mb-6" />
            <h2 className="text-xl md:text-5xl font-bold tracking-wide uppercase">The Smart Way to Learn Music</h2>
          </div>
        </div>
        {/* Right half: stacked white sections */}
        <div className="flex flex-col divide-y divide-black">
          {/* Section 1 */}
          <div className="bg-white text-black flex items-center justify-center p-8 min-h-screen -mt-px">
            <div className="max-w-xl w-full text-lg md:text-[1.6rem] text-justify leading-normal px-6 md:px-10">
              Maestera is an <span className="text-red-600 font-bold">innovative music education platform</span>. designed to transform the way people connect and learn through music. Built on the principles of <span className="text-red-600 font-bold">flexibility, accessibility, and personalization</span>, Maestera caters to the unique needs of music enthusiasts and learners.
            </div>
          </div>
          {/* Section 2 */}
          <div className="bg-white text-black flex items-center justify-center p-8 min-h-screen">
            <div className="max-w-xl w-full text-lg md:text-[1.6rem] text-justify leading-normal px-6 md:px-10">
              Unlike traditional platforms, Maestera doesn’t limit you to fixed courses or pricing. We offer a <span className="text-red-600 font-bold">highly customizable learning experience</span>, tailored to your preferences—whether it’s your choice of instrument, budget, genre, location, or preferred schedule. This allows us to connect learners and teachers from all walks of life, ensuring music education is accessible to <span className="text-red-600 font-bold">everyone, everywhere.</span>
            </div>
          </div>
          {/* Section 3 */}
          <div className="bg-white text-black flex items-center justify-center p-8 min-h-screen">
            <div className="max-w-xl w-full text-lg md:text-[1.6rem] text-justify leading-normal px-6 md:px-10">
              Maestera offers <span className="text-red-600 font-bold">lessons in a vast array of instruments</span>, covering everything from the most popular to the unique. In addition, we provide <span className="text-red-600 font-bold">comprehensive education in non-instrumental</span> subjects like music production, orchestration, composition, and more, ensuring a <span className="text-red-600 font-bold">complete learning experience</span> for every music enthusiast.
            </div>
          </div>
          {/* Section 4 */}
          <div className="bg-white text-black flex items-center justify-center p-8 min-h-screen">
            <div className="max-w-xl w-full text-lg md:text-[1.6rem] text-justify leading-normal px-6 md:px-10">
              Whether you’re a toddler discovering your love for music, an adult refining your skills, a professional looking to enhance your expertise, or someone with special needs exploring new creative outlets, Maestera ensures your journey is as unique as your goals.<span className="text-red-600 font-bold"> Music is for everyone, at any age or stage in life.</span> With Maestera, the world of music is limitless. <span className="text-red-600 font-bold">Your path to creativity starts here.</span>
            </div>
          </div>
        </div>
      </section>

      {/* Why Choose Maestera Section */}
      <section className="bg-white text-black py-20 px-6 md:px-12">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-4xl md:text-[2.6rem] font-extrabold text-center mb-16">WHY CHOOSE MAESTERA?</h2>
          
          <div className="relative overflow-hidden">
            <div 
              className="flex transition-transform duration-500 ease-in-out"
              style={{ transform: `translateX(-${gifIndex * (100 / 3)}%)` }}
            >
              {gifs.map((gif, index) => (
                <div key={index} className="w-1/3 flex-shrink-0 px-3">
                  <div className="text-center">
                    <div className="mb-6">
                      <img 
                        src={gif.src} 
                        alt={gif.title}
                        className="w-full h-64 object-cover rounded-lg mx-auto"
                      />
                    </div>
                    <h3 className="text-lg md:text-xl font-bold mb-3 text-red-600 uppercase tracking-wide">
                      {gif.title}
                    </h3>
                    <p className="text-sm md:text-lg text-gray-700 leading-relaxed">
                      {gif.description}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Black Text Reveal Section */}
      <section id="text-reveal-section" className="bg-black text-white py-12 px-6 md:px-12">

        <div className="max-w-4xl mx-auto">
          <div className="text-3xl md:text-3xl font-bold leading-loose">
            {renderTextWithReveal()}
          </div>
        </div>
      </section>
   {/* Navigation cards section */}
<section className="bg-white py-10 px-4">
  <div className="w-full mx-auto">
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">

      {/* Column 1 */}
      <div className="grid gap-4">
        {/* Big card */}
        <div className="bg-white h-84 rounded-xl shadow-[0_2px_10px_rgba(0,0,0,0.12)] hover:shadow-[0_3px_16px_rgba(0,0,0,0.15)] transition-all duration-300 cursor-pointer">
           <div className="flex flex-col items-center justify-center h-full w-full">
    <h2 className="text-2xl md:text-3xl font-bold text-black text-center">
      MEET THE TEAM
    </h2>
  </div>
        </div>

        <div className="grid grid-cols-2 gap-4">
          {/* Tall left card */}
          <Link href="/contact-us">
          <div className="bg-white h-100 rounded-xl shadow-[0_2px_10px_rgba(0,0,0,0.12)] hover:shadow-[0_3px_16px_rgba(0,0,0,0.15)] transition-all duration-300 cursor-pointer">
            <div className="flex flex-col items-center justify-center h-full w-full">
    <h2 className="text-2xl md:text-[1.6rem] font-medium text-black text-center">
      Contact Us
    </h2>
  </div>
          </div></Link>

          {/* Right stacked small cards */}
          <div className="grid gap-4">
            <Link href="/become-a-teacher">
            <div className="bg-white h-64 rounded-xl shadow-[0_2px_10px_rgba(0,0,0,0.12)] hover:shadow-[0_3px_16px_rgba(0,0,0,0.15)] transition-all duration-300 cursor-pointer">
              <div className="flex flex-col items-center justify-center h-full w-full">
    <h2 className="text-2xl md:text-[1.6rem] font-medium text-black text-center">
      Become a Teacher
    </h2>
  </div>
            </div>
            </Link>
            <div className="bg-white h-32 rounded-xl shadow-[0_2px_10px_rgba(0,0,0,0.12)] hover:shadow-[0_3px_16px_rgba(0,0,0,0.15)] transition-all duration-300 cursor-pointer">
              <div className="flex flex-col items-center justify-center h-full w-full">
    <h2 className="text-2xl md:text-[1.6rem] font-medium text-black text-center">
      FAQs
    </h2>
  </div>
            </div>
          </div>
        </div>
      </div>

      {/* Column 2 */}
      <div className="grid grid-cols-3 gap-4">
        {/* Row 1 */}
        <div className="bg-white h-64 rounded-xl shadow-[0_2px_10px_rgba(0,0,0,0.12)] hover:shadow-[0_3px_16px_rgba(0,0,0,0.15)] transition-all duration-300 cursor-pointer col-span-1">
          <img
                src="/images/about-us/piano.png" 
                alt="Piano"
                className="w-full h-full object-contain rounded-xl"
              />
        </div>
        <div className="bg-white h-64 rounded-xl shadow-[0_2px_10px_rgba(0,0,0,0.12)] hover:shadow-[0_3px_16px_rgba(0,0,0,0.15)] transition-all duration-300 cursor-pointer col-span-2">
          <div className="flex flex-col items-center justify-center h-full w-full">
    <h2 className="text-2xl md:text-[1.6rem] font-medium text-black text-center">
      Start your Music Learning Journey Today
    </h2>
  </div>
        </div>

        {/* Row 2 */}
        <div className="bg-white h-52 rounded-xl shadow-[0_2px_10px_rgba(0,0,0,0.12)] hover:shadow-[0_3px_16px_rgba(0,0,0,0.15)] transition-all duration-300 cursor-pointer col-span-2">
           <img
                src="/images/about-us/music.png" 
                alt="Piano"
                className="w-full h-full object-contain rounded-xl"
              />
        </div>
        <div className="bg-white h-52 rounded-xl shadow-[0_2px_10px_rgba(0,0,0,0.12)] hover:shadow-[0_3px_16px_rgba(0,0,0,0.15)] transition-all duration-300 cursor-pointer col-span-1">
          <div className="flex flex-col items-center justify-center px-6 h-full w-full">
    <h2 className="text-2xl md:text-[1.6rem] font-medium text-black text-center">
      Coming Soon - Group Classes
    </h2>
  </div>
        </div>

        {/* Row 3 */}
        <div className="bg-white h-64 rounded-xl shadow-[0_2px_10px_rgba(0,0,0,0.12)] hover:shadow-[0_3px_16px_rgba(0,0,0,0.15)] transition-all duration-300 cursor-pointer col-span-1">
          <div className="flex flex-col items-center justify-center h-full w-full">
    <h2 className="text-2xl md:text-[1.6rem] font-medium text-black text-center">
      Offers
    </h2>
    </div>
        </div>
        <div className="bg-white h-64 rounded-xl shadow-[0_2px_10px_rgba(0,0,0,0.12)] hover:shadow-[0_3px_16px_rgba(0,0,0,0.15)] transition-all duration-300 cursor-pointer col-span-2">
          <img
                src="/images/Home-page-banner.1.png" 
                alt="Team"
                className="w-full h-full object-cover rounded-xl"
              />
        </div>
      </div>

    </div>
  </div>
</section>







      {/* Mute/Unmute Floating Button */}
      <button
        onClick={() => setMuted((m) => !m)}
        aria-label={muted ? "Unmute video" : "Mute video"}
        className="fixed bottom-6 right-6 z-20 w-14 h-14 rounded-full bg-white/40 text-white backdrop-blur flex items-center justify-center shadow-md transition"
      >
        {muted ? (
          <VolumeX className="w-6 h-6" />
        ) : (
          <Volume2 className="w-6 h-6" />
        )}
      </button>
      <Footer />
    </main>
  )
}

"use client"

import Header from "../components/Header"
import { useEffect, useState } from "react"

export default function BecomeTeacher() {
  const [gifIndex, setGifIndex] = useState(0)
  const [testimonialIndex, setTestimonialIndex] = useState(0)

  const teachers = [
    { id: 1, name: "Amritha Rajesh Chelat", image: "/images/teachers/carissa-profile.png" },
    { id: 2, name: "CH Eshwar", image: "/images/teachers/carissa-profile.png" },
    { id: 3, name: "Naveena S", image: "/images/teachers/carissa-profile.png" },
    { id: 4, name: "Aditya Ganesh", image: "/images/teachers/carissa-profile.png" },
    { id: 5, name: "Prakriti Pandey", image: "/images/teachers/carissa-profile.png" },
    { id: 6, name: "Aastha Mohapatra", image: "/images/teachers/carissa-profile.png" },
  ]

  const gifs = [
    {
      src: "/images/become-a-teacher/gif/Decide-Your-Own-Pricing-black.gif",
      title: "PERSONALIZED LEARNING EXPERIENCE",
      description:
        "Your lessons are tailored to your instrument, genre, budget, location, and schedule—music that fits your life.",
    },
    {
      src: "/images/become-a-teacher/gif/Diverse-Opportunities-black.gif",
      title: "DIVERSE OFFERINGS",
      description:
        "Learn everything from piano and trumpet to DJing, production, and composition—all in one place.",
    },
    {
      src: "/images/become-a-teacher/gif/Flexibility-Black.gif",
      title: "FOR ALL SKILL LEVELS",
      description:
        "Whether you're starting out or refining your craft, Maestera connects you with the right teacher.",
    },
    {
      src: "/images/become-a-teacher/gif/Global-Reach-black.gif",
      title: "GLOBAL AND LOCAL ACCESS",
      description:
        "Find teachers near you or around the world—wherever you prefer to learn.",
    },
    {
      src: "/images/become-a-teacher/gif/Personalized-Matches-black.gif",
      title: "HOLISTIC AND INCLUSIVE APPROACH",
      description:
        "Explore performance, theory, and genres from Indian classical to jazz and experimental music.",
    },
    {
      src: "/images/become-a-teacher/gif/Supportive-Community-black.gif",
      title: "SUPPORT EVERY STEP OF THE WAY",
      description:
        "Can't find a teacher? We'll personally help match you with one who fits your needs.",
    },
  ]

  const testimonials = [
    {
      text: "Maestera is a fantastic platform where we can connect with like minded teachers. We can follow our interests and get connected to the right teacher. I learnt western singing from Pravalikaa and Carnatic singing from Arvindh Sithu. Both of them were very experienced teachers and I have improved my singing from them.",
      name: "Saatvik Sannuthi",
    },
    {
      text: "It's been an absolute joy learning with Freya at Maestera. We've been working through the fundamentals and refining my vocal technique, and I’m seeing real progress. Freya is knowledgeable, supportive, and easy to work with.",
      name: "Rhea Jain",
    },
  ]

  // Auto-scroll GIFs
  useEffect(() => {
    const interval = setInterval(() => {
      setGifIndex((prev) => (prev + 1) % 4)
    }, 5000)
    return () => clearInterval(interval)
  }, [])

  // Auto-scroll testimonials
  useEffect(() => {
    const interval = setInterval(() => {
      setTestimonialIndex((prev) => (prev + 1) % testimonials.length)
    }, 5000)
    return () => clearInterval(interval)
  }, [testimonials.length])

  return (
    <div className="w-full text-white bg-black">
      {/* Header */}
      <Header />

      {/* Hero Section */}
      <section
        className="flex flex-col items-center justify-center text-center px-6 py-20 bg-cover bg-center relative min-h-screen"
        style={{
          backgroundImage: "url('/images/become-a-teacher/become-teacher.png')",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <div className="translate-y-10">
          <h1 className="text-white text-3xl md:text-5xl font-bold tracking-widest leading-snug">
            INSPIRE THE NEXT
          </h1>

          <h2
            className="text-red-600 text-5xl md:text-5xl font-bold tracking-widest leading-snug mt-3"
            style={{ textShadow: "4px 4px 0 rgba(0,0,0,0.8)" }}
          >
            GENERATION OF MUSICIANS
          </h2>

          <a
            href="https://maestera-onboarding.vercel.app/"
            target="_blank"
            rel="noopener noreferrer"
            className="mt-8 inline-block bg-white text-black font-semibold px-8 py-3 rounded-full shadow-md transition hover:bg-gray-200"
          >
            Sign Up
          </a>
        </div>
      </section>

      {/* Everything below hero is black */}
      <section className="w-full bg-black text-white px-6 md:px-12 pt-20 pb-20 relative">
        <h2 className="text-3xl md:text-4xl font-bold tracking-wide px-8 text-left">
          YOUR PLATFORM,
          <span className="text-red-600 ml-2 font-bold drop-shadow-[3px_3px_0px_rgba(0,0,0,0.6)]">
            YOUR RULES
          </span>
        </h2>

        <p className="mt-8 text-lg md:text-[1.9rem] font-bold leading-relaxed px-8 text-left">
          At Maestera, we believe that teachers thrive when they have<br />
          the{" "}
          <span className="bg-red-600 text-white px-2 py-1">
            freedom to teach their way.
          </span>{" "}
          Whether it’s your field of expertise, teaching style, schedule, or pricing,
          you’re in control.<br />
          We simply provide the platform to connect.
        </p>

        <h1 className="mt-16 text-3xl md:text-[6.5rem] font-bold text-white/50 tracking-wide text-center">
          TEACHERS
        </h1>

        {/* Randomly placed teacher cards */}
        <div className="relative w-full h-[1500px] mt-20">
          {teachers.map((teacher, index) => (
            <div
              key={index}
              className="absolute flex flex-col items-center text-center transition-transform duration-300 hover:scale-105"
              style={{
                top: ["10%", "20%", "40%", "60%", "80%", "95%"][index],
                left: ["25%", "65%", "45%", "75%", "35%", "55%"][index],
                transform: "translate(-50%, -50%)",
              }}
            >
              <img
                src={teacher.image}
                alt={teacher.name}
                className="w-48 h-48 md:w-64 md:h-64 object-cover rounded-3xl shadow-lg"
              />
              <h3 className="text-lg font-semibold text-white mt-3">{teacher.name}</h3>
            </div>
          ))}
        </div>

        <h1 className="mt-28 text-3xl md:text-[6.0rem] font-bold text-white/50 tracking-wide text-center">
          WHY JOIN MAESTERA?
        </h1>

        {/* GIF Section */}
        <div className="relative mt-16 overflow-hidden">
          <div
            className="flex transition-transform duration-500 ease-in-out"
            style={{ transform: `translateX(-${gifIndex * (100 / 3)}%)` }}
          >
            {gifs.map((gif, index) => (
              <div key={index} className="w-1/3 flex-shrink-0 px-3">
                <div className="text-center">
                  <div className="mb-6">
                    <img
                      src={gif.src}
                      alt={gif.title}
                      className="w-full h-64 object-cover rounded-lg mx-auto"
                    />
                  </div>
                  <h3 className="text-lg md:text-xl font-bold mb-3 text-red-600 uppercase tracking-wide">
                    {gif.title}
                  </h3>
                  <p className="text-sm md:text-lg text-gray-400 leading-relaxed">
                    {gif.description}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* TESTIMONIALS SECTION */}
        {/* TESTIMONIALS SECTION */}
        <section className="bg-black text-white mt-8 text-center py-8 px-2 md:px-4 relative overflow-hidden">
          <h1 className="text-3xl md:text-[6.6rem] font-bold text-white/50 tracking-wide text-center">
            TESTIMONIALS
          </h1>

          {/* Fixed-height container to prevent jumping */}
          <div className="max-w-4xl mx-auto py-16 relative transition-all duration-700 ease-in-out h-[380px] flex flex-col justify-center items-center">
            <div className="transition-opacity duration-700 ease-in-out text-center">
              <p className="text-xl md:text-2xl leading-relaxed mb-6 italic text-white/90">
                “{testimonials[testimonialIndex].text}”
              </p>
              <h4 className="text-xl font-semibold text-red-500">
                — {testimonials[testimonialIndex].name}
              </h4>
            </div>
          </div>
        </section>


        {/* FAQ SECTION */}
        <section className="w-full bg-black text-white px-6 md:px-6 pt-12 pb-4">
          <h1 className="text-center text-3xl md:text-5xl font-bold tracking-wide mb-16">
            FREQUENTLY ASKED QUESTIONS <span className="text-red-600">»</span>
          </h1>

          <FAQItem
            question="Who Can Join As A Music Teacher?"
            answer="Anyone with proficiency in teaching a musical instrument, vocals, or music theory and a passion for sharing their knowledge can join. Whether you're a certified instructor, a professional musician, or an experienced performer, we'd love to have you on board."
          />

          <FAQItem
            question="How Do I Sign Up As A Teacher?"
            answer={
              <>
                Signing up is easy! Create a profile on our website by providing your personal details, musical expertise, teaching experience, availability, and preferred teaching format (online, in-person, or both). Once approved, you'll be ready to connect with students.{" "}
                <a
                  href="https://maestera-onboarding.vercel.app/"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-white font-semibold px-1 py-1 rounded-full ml-1 transition"
                >
                  "sign up"
                </a>
                .
              </>
            }
          />


          <FAQItem
            question="Do I Need Prior Teaching Experience?"
            answer="Teaching experience is helpful but not mandatory. If you are skilled, passionate, and committed to helping students learn, you're welcome at Maestera."
          />

          <FAQItem
            question="Can I Teach Online Or In Person?"
            answer="You can choose either format or both. Maestera supports teachers who want to teach online, in-person, or in a hybrid model."
          />
        </section>
      </section>
    </div>
  )
}

// ===== FAQ COMPONENT =====
function FAQItem({
  question,
  answer,
}: {
  question: string
  answer: React.ReactNode
}) {
  const [open, setOpen] = useState(false)

  return (
    <div className="mb-6">
      <button
        onClick={() => setOpen(!open)}
        className="w-full bg-[#222] text-left px-6 py-6 rounded-lg flex justify-between items-center text-xl md:text-2xl font-semibold"
      >
        <span>{question}</span>
        <span className="text-3xl">{open ? "–" : "+"}</span>
      </button>

      <div
        className={`overflow-hidden transition-all duration-500 ${open ? "max-h-[300px] opacity-100 py-6" : "max-h-0 opacity-0"
          } bg-[#111] px-6 rounded-b-lg text-lg md:text-xl`}
      >
        {answer}
      </div>
    </div>
  )
}

